import React, { Component } from "react";
import { useFormik } from "formik";

class appointment extends Component {
  state = {
    prospect: {
      email: "",
      name: "",
      accountNumber: "",
      reduction: "",
    },
  };
  handleChangeName = (e) => {
    const prospect = { ...this.state.prospect };
    prospect.name = e.target.value;
    this.setState({ prospect });
  };
  handleChangeEmail = (e) => {
    const prospect = { ...this.state.prospect };
    prospect.email = e.target.value;
    this.setState({ prospect });
  };
  handleChangeAccountNumber = (e) => {
    const prospect = { ...this.state.prospect };
    prospect.accountNumber = e.target.value;
    this.setState({ prospect });
  };
  render() {
    return (
      // Appointment section start
      <section className="appointment">
        <div className="appointment-wrap">
          <figure>
            <img src={require("../assets/images/resto.jpg")} alt="" />
          </figure>
          <div className="appointment-form">
            <form action="#">
              {/* <div className="form-field half-width">
                <input type="date" placeholder="date" />
    </div>*/}
              <div className="form-field half-width">
                <input
                  type="text"
                  placeholder="NOM"
                  required
                  id="name"
                  value={this.state.prospect.name}
                  onChange={this.handleChangeName}
                />
                <input
                  type="email"
                  placeholder="Adresse Email"
                  required
                  id="email"
                  value={this.state.prospect.email}
                  onChange={this.handleChangeEmail}
                />
              </div>
              <div className="form-field half-width">
                <div className="select-field">
                  <input
                    type="number"
                    placeholder="NOMBRE DE COMPTE"
                    required
                    id="accountNumber"
                    value={this.state.prospect.accountNumber}
                    onChange={this.handleChangeAccountNumber}
                  />
                </div>
              </div>
              <div className="form-field half-width">
                <div className="select-field">
                  <select>
                    <option>Selectionner le %age de réduction</option>
                    <option>0%</option>
                    <option>10%</option>
                    <option>20%</option>
                    <option>30%</option>
                    <option>40%</option>
                    <option>50%</option>
                    <option>60%</option>
                    <option>70%</option>
                    <option>80%</option>
                    <option>90%</option>
                    <option>100%</option>
                  </select>
                </div>
              </div>
              {/*<div className="form-field">
                <textarea name="notes" placeholder="Your notes"></textarea>
    </div>*/}
              <button className="btn btn-round">Envoyer la proposition</button>
            </form>
          </div>
        </div>
      </section>
      // Appointment section end
    );
  }
}

export default appointment;
