import React, { useEffect, useState } from "react";
import { Link, useHistory, useLocation } from "react-router-dom";
import { auth, logInWithEmailAndPassword } from "../firebase";
import { useAuthState } from "react-firebase-hooks/auth";
import "../Login.css";
import { getDatabase, ref, set, onValue } from "firebase/database";

function useQuery() {
  const { search } = useLocation();

  return React.useMemo(() => new URLSearchParams(search), [search]);
}

function RegisterComponent() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [user, loading, error] = useAuthState(auth);
  const [code, setCode] = useState(false);
  const history = useHistory();
  let query = useQuery();

  const register = () => {
    if (!name) alert("Merci d'entrer votre nom");
    if (!code) {
      toast.warn(
        "Vous devez contacter notre service client pour créer un compte !",
        {
          position: toast.POSITION.TOP_CENTER,
        }
      );
    } else {
      registerWithEmailAndPassword(name, email, password);
    }
  };

  useEffect(() => {
    const code = query.get("code");
    setCode(code);
    if (loading) {
      // maybe trigger a loading screen
      return;
    }
    if (user) {
      if (code) {
        // enregistrer uid prospect
        get(child(dbRef, `prospectEmail/${code}`)).then((snapshot) => {
          if (snapshot.exists()) {
            const data = snapshot.val();
            set(ref(db, "prospect/" + user.uid), data);

            toast.success("Votre compte a bien été créé !", {
              position: toast.POSITION.TOP_CENTER,
            });
            history.push({
              pathname: "/prospect",
              search: "?code=" + code,
              state: { detail: data },
            });
          } else {
            console.log("No data available");
          }
        });
        /*  const db = getDatabase();
        const prospectRef = ref(db, "prospectEmail/" + code);
        onValue(prospectRef, (snapshot) => {
          const data = snapshot.val();

          set(ref(db, "prospect/" + user.uid), data);
          history.push({
            pathname: "/prospect",
            search: "?code=" + code,
            state: { detail: data },
          });

        }); */
      } else {
        //history.push("/marketplace");
      }
    }
  }, [user, loading]);
  return (
    <div className="register__container mt-3">
      <input
        type="text"
        className="register__textBox"
        value={name}
        onChange={(e) => setName(e.target.value)}
        placeholder="Votre nom"
      />
      <input
        type="text"
        className="register__textBox"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        placeholder="Adresse E-mail"
      />
      <input
        type="password"
        className="register__textBox"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        placeholder="Mot de passe"
      />
      <button className="register__btn" onClick={register}>
        S'enregistrer
      </button>
      <div>
        Déjà un compte ? <Link to={"/?code=" + code}>Se connecter</Link>{" "}
        maintenant.
      </div>
    </div>
  );
}

export default RegisterComponent;
