import React, { useRef } from "react";
import emailjs from "@emailjs/browser";

const Appointment = () => {
  const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();
    emailjs
      .sendForm(
        "service_281tpu8",
        "template_s7l53t7",
        e.target,
        "user_cs48fHhUC3ofs28N0k1oK"
      )
      .then(
        (result) => {
          console.log(result.text);
        },
        (error) => {
          console.log(error.text);
        }
      );
  };

  return (
    <section className="appointment">
      <div className="appointment-wrap">
        <figure>
          <img src={require("../assets/images/resto.jpg")} alt="" />
        </figure>
        <div className="appointment-form">
          <form ref={form} onSubmit={sendEmail}>
            <div className="form-field half-width">
              <input
                type="text"
                placeholder="NOM"
                required
                id="name"
                value={this.state.prospect.name}
                onChange={this.handleChangeName}
              />
              <input
                type="email"
                placeholder="Adresse Email"
                required
                id="email"
                value={this.state.prospect.email}
                onChange={this.handleChangeEmail}
              />
            </div>
            <div className="form-field half-width">
              <div className="select-field">
                <input
                  type="number"
                  placeholder="NOMBRE DE COMPTE"
                  required
                  id="accountNumber"
                  value={this.state.prospect.accountNumber}
                  onChange={this.handleChangeAccountNumber}
                />
              </div>
            </div>
            <div className="form-field half-width">
              <div className="select-field">
                <select
                  onChange={this.handleChangeAccountReduction}
                  id="reduction"
                  value={this.state.prospect.reduction}
                >
                  <option>Selectionner le %age de réduction</option>
                  <option value={0}>0%</option>
                  <option value={10}>10%</option>
                  <option value={20}>20%</option>
                  <option value={30}>30%</option>
                  <option value={40}>40%</option>
                  <option value={50}>50%</option>
                  <option value={60}>60%</option>
                  <option value={70}>70%</option>
                  <option value={80}>80%</option>
                  <option value={90}>90%</option>
                  <option value={100}>100%</option>
                </select>
              </div>
            </div>
            <button
              className="btn btn-round"
              type="submit"
              className="btn btn-primary"
              onClick={this.handleSend}
            >
              Envoyer la proposition
            </button>
          </form>
        </div>
      </div>
    </section>
  );
};

