import React, { Component } from "react";
import { NavLink, withRouter } from "react-router-dom";
import { auth, logInWithEmailAndPassword } from "../firebase";
import { getDatabase, ref, onValue, child, get } from "firebase/database";

class Navbar extends Component {
  state = {
    menuOpen: false,
    admin: true,
  };

  componentDidMount() {
    auth.onAuthStateChanged(function (user) {
      //this.setState(user);
      const uid = user.uid;
      const db = getDatabase();
      const dbRef = ref(getDatabase());
      get(child(dbRef, `users/${uid}`)).then((snapshot) => {
        if (snapshot.exists()) {
          const data = snapshot.val();
          if (
            data &&
            data.type &&
            (data.type == "admin" || data.type == "commercial")
          ) {
            this.setState({ admin: true });
          } else {
            this.setState({ admin: false });
          }
        } else {
          console.log("No data available");
        }
      });
    });
  }

  updateState = () => {
    this.setState({ admin: true });
  };

  // isHome =
  // absHeader = ( this.props.location.pathname === "/") ? ' abs-header' : '';
  // right = ( this.props.location.pathname === '/') ? '' : ' text-right';

  menuToggleHandler = () => {
    this.setState((prevState) => {
      return { menuOpen: !prevState.menuOpen };
    });
  };

  // componentWillUnmount() {
  //     this.clearStateHandler()
  // }

  clearStateHandler = () => {
    if (this.state.menuOpen) {
      this.setState({
        menuOpen: false,
      });
    }
  };

  render() {
    let absHeader = this.props.location.pathname === "/" ? " abs-header" : "";
    let right = this.props.location.pathname === "/" ? "" : " text-right";

    return (
      // Header start
      <header className={"header" + absHeader}>
        <div className="container">
          <div className="row align-items-end">
            <div className="col-md-3">
              <NavLink to="/" className="logo">
                <img src={require("../assets/images/logo.png")} alt="" />
              </NavLink>
            </div>
            <div className={"col-md-9" + right}>
              <nav className="primary-menu">
                <button
                  className="mobile-menu"
                  onClick={this.menuToggleHandler}
                >
                  <i className="ti-menu"></i>
                </button>
                {false && (
                  <ul className={this.state.menuOpen ? "active" : ""}>
                    {/*<li>
                    <NavLink to="/" onClick={this.clearStateHandler}>
                      ACCUEIL
                    </NavLink>
                 </li>*/}
                    <li>
                      <NavLink to="/prospect" onClick={this.clearStateHandler}>
                        proposition en cours
                      </NavLink>
                    </li>
                    <li>
                      <NavLink to="/gestion" onClick={this.clearStateHandler}>
                        MES COMPTES UNEXTRA
                      </NavLink>
                    </li>
                    <li>
                      <NavLink to="/achat" onClick={this.clearStateHandler}>
                        ACHAT DE COMPTES
                      </NavLink>
                    </li>
                  </ul>
                )}
              </nav>
            </div>
          </div>
        </div>
      </header>
      // Header start
    );
  }
}

export default withRouter(Navbar);
