import React, { useEffect, useState } from "react";
import { useAuthState } from "react-firebase-hooks/auth";
import { auth, registerWithEmailAndPassword } from "../../firebase";
import "../../Register.css";
import { getDatabase, ref, set, onValue } from "firebase/database";
import { Link, useHistory, useLocation } from "react-router-dom";
import { toast } from "react-toastify";

function useQuery() {
  const { search } = useLocation();

  return React.useMemo(() => new URLSearchParams(search), [search]);
}

function Register() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState("");
  const [user, loading, error] = useAuthState(auth);
  const history = useHistory();
  const [code, setCode] = useState(false);
  let query = useQuery();
  const dbRef = ref(getDatabase());

  const register = () => {
    if (!name) alert("Merci d'entrer votre nom");
    if (!code) {
      toast.warn(
        "Vous devez contacter notre service client pour créer un compte !",
        {
          position: toast.POSITION.TOP_CENTER,
        }
      );
    } else {
      registerWithEmailAndPassword(name, email, password);
    }
  };

  useEffect(() => {
    const code = query.get("code");
    setCode(code);
    if (loading) {
      // maybe trigger a loading screen
      return;
    }
    if (user) {
      if (code) {
        // enregistrer uid prospect
        get(child(dbRef, `prospectEmail/${code}`)).then((snapshot) => {
          if (snapshot.exists()) {
            const data = snapshot.val();
            set(ref(db, "prospect/" + user.uid), data);

            toast.success("Votre compte a bien été créé !", {
              position: toast.POSITION.TOP_CENTER,
            });
          } else {
            console.log("No data available");
          }
        });
        /*  const db = getDatabase();
        const prospectRef = ref(db, "prospectEmail/" + code);
        onValue(prospectRef, (snapshot) => {
          const data = snapshot.val();

          set(ref(db, "prospect/" + user.uid), data);
          history.push({
            pathname: "/prospect",
            search: "?code=" + code,
            state: { detail: data },
          });

        }); */
      } else {
        //history.push("/marketplace");
      }
    }
  }, [user, loading]);

  return (
    <React.Fragment>
      <section className="banner">
        <div className="spa-img">
          <img src={require("../../assets/images/ronde.png")} alt="" />
        </div>
        <div className="container">
          <div className="row">
            <div className="col-lg-6">
              <div className="banner-content">
                <h2>Créé ton compte UNEXTRA</h2>
                <div className="register__container mt-3">
                  <input
                    type="text"
                    className="register__textBox"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    placeholder="Votre nom"
                  />
                  <input
                    type="text"
                    className="register__textBox"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    placeholder="Adresse E-mail"
                  />
                  <input
                    type="password"
                    className="register__textBox"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    placeholder="Mot de passe"
                  />
                  <button className="register__btn" onClick={register}>
                    S'enregistrer
                  </button>
                  <div>
                    Déjà un compte ?{" "}
                    <Link to={"/?code=" + code}>Se connecter</Link> maintenant.
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    </React.Fragment>
  );
}

export default Register;
