import React, { useRef, useState } from "react";
import emailjs from "@emailjs/browser";
import { getDatabase, ref, set } from "firebase/database";
import { v4 as uuidv4 } from "uuid";

const Appointment = () => {
  const [code, setCode] = useState(uuidv4());
  const [email, setEmail] = useState("");
  const [accountNumber, setAccountNumber] = useState("");
  const [reduction, setReduction] = useState("");
  const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();
    emailjs
      .sendForm(
        "service_281tpu8",
        "template_s7l53t7",
        e.target,
        "user_cs48fHhUC3ofs28N0k1oK"
      )
      .then(
        (result) => {
          console.log(result.text);
          const db = getDatabase();
          console.log("code", code);
          set(ref(db, "prospectEmail/" + code), {
            email,
            reduction,
            accountNumber,
          });
        },
        (error) => {
          console.log(error.text);
        }
      );
  };

  return (
    <section className="appointment">
      <div className="appointment-wrap">
        <figure>
          <img src={require("../assets/images/resto.jpg")} alt="" />
        </figure>
        <div className="appointment-form">
          <form ref={form} onSubmit={sendEmail}>
            <div className="form-field half-width">
              <input
                type="text"
                placeholder="code"
                required
                value={code}
                id="email"
                name="email"
              />
              <input
                type="text"
                placeholder="NOM"
                required
                id="name"
                name="name"
              />
              <input
                type="email"
                placeholder="Adresse Email"
                required
                onChange={(e) => setEmail(e.target.value)}
                id="email"
                name="email"
              />
            </div>
            <div className="form-field half-width">
              <div className="select-field">
                <input
                  type="number"
                  placeholder="NOMBRE DE COMPTE"
                  required
                  onChange={(e) => setAccountNumber(e.target.value)}
                  id="accountNumber"
                  name="accountNumber"
                />
              </div>
            </div>
            <div className="form-field half-width">
              <div className="select-field">
                <select
                  id="reduction"
                  name="reduction"
                  onChange={(e) => setReduction(e.target.value)}
                >
                  <option>Selectionner le %age de r√©duction</option>
                  <option value={0}>0%</option>
                  <option value={10}>10%</option>
                  <option value={20}>20%</option>
                  <option value={30}>30%</option>
                  <option value={40}>40%</option>
                  <option value={50}>50%</option>
                  <option value={60}>60%</option>
                  <option value={70}>70%</option>
                  <option value={80}>80%</option>
                  <option value={90}>90%</option>
                  <option value={100}>100%</option>
                </select>
              </div>
            </div>
            <input
              className="btn btn-round"
              type="submit"
              className="btn btn-primary"
              value="Envoyer la proposition"
            />
          </form>
        </div>
      </div>
    </section>
  );
};

export default Appointment;
