import React, { useState, Fragment, useEffect } from "react";
import { useLocation, useHistory } from "react-router-dom";
import { getDatabase, ref, onValue } from "firebase/database";
import { useAuthState } from "react-firebase-hooks/auth";
import { auth } from "../../firebase";

function useQuery() {
  const { search } = useLocation();

  return React.useMemo(() => new URLSearchParams(search), [search]);
}

const Gestion = (props) => {
  const [user, loading, error] = useAuthState(auth);
  const [userLogged, setUserLogged] = useState(false);
  const [compte, setCompte] = useState(false);
  let query = useQuery();
  let location = useLocation();
  const db = getDatabase();
  const history = useHistory();
  useEffect(() => {
    auth.onAuthStateChanged(function (user) {
      setUserLogged(user);
      if (true) {
        const prospectRef = ref(db, "prospect/" + user.uid);
        onValue(prospectRef, (snapshot) => {
          const data = snapshot.val();
          console.log("data", data);
          setCompte(data.restant);
        });
      }
    });
  }, []);
  return (
    <Fragment>
      <section className="procedures">
        <div className="container">
          <div className="row">
            <div className="col-lg-8 col-md-10 m-auto">
              <div className="sec-heading">
                <h3 className="sec-title">Gestion de mes comptes UNEXTRA</h3>
                <p>Suivez l'évolution de vos comptes.</p>
              </div>
            </div>
          </div>
        </div>
      </section>
      <section className="appointment">
        <div className="appointment-wrap">
          <figure>
            <img src={require("../../assets/images/resto.jpg")} alt="" />
          </figure>
          <div className="appointment-form">
            <form>
              <div className="form-field">
                <div className="select-field">
                  <div>NOMBRE DE COMPTES RESTANTS : {compte}</div>
                  <ul class="list-group">
                    <li class="list-group-item d-flex justify-content-between align-items-center">
                      <div>
                        <div>Code à partager :</div>
                        <div className="d-flex justify-content-between">
                          <div class="font-italic" style={{ fontSize: 13 }}>
                            Créé le
                          </div>
                          <div
                            class="font-italic"
                            style={{ fontSize: 13, marginLeft: 100 }}
                          >
                            Validé le
                          </div>
                        </div>
                      </div>
                      <button
                        type="button"
                        class="btn btn-danger"
                        style={{
                          backgroundColor: "red",
                          paddingTop: 5,
                          paddingBottom: 5,
                        }}
                      >
                        Supprimer
                      </button>
                    </li>
                    <li class="list-group-item">Dapibus ac facilisis in</li>
                    <li class="list-group-item">Morbi leo risus</li>
                    <li class="list-group-item">Porta ac consectetur ac</li>
                    <li class="list-group-item">Vestibulum at eros</li>
                  </ul>
                </div>
              </div>
              <button
                className="btn btn-round"
                type="submit"
                className="btn btn-primary mt-3"
              >
                Accepter la proposition
              </button>
            </form>
          </div>
        </div>
      </section>
    </Fragment>
  );
};

export default Gestion;
